import styled from 'styled-components';

export const LoginWrapper = styled.div`
    position:absolute;
    left:0;
    right:0;
    bottom:0;
    top:56px;
    background:#eee;
    z-index:0;
`;
export const LoginBox = styled.div`
    width:400px;
    height:200px;
    margin:50px auto;
    background:#fff;
    box-shadow: 0 0 8px rgba(0,0,0,.1);
`;
